function bubbleSort(arr) {
  var len = arr.length;
  for (var i = len-1; i>=0; i--){
    for(var j = 1; j<=i; j++){
      if(arr[j-1]>arr[j]){
          var temp = arr[j-1];
          arr[j-1] = arr[j];
          arr[j] = temp;
       }
    }
  }
  return arr;
}

// Ejemplo de uso:
var myArray = [3, 6, 1, 8, 2, 10, 5];
bubbleSort(myArray);
console.log(myArray); // Resultado: [1, 2, 3, 5, 6, 8, 10]
